import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles.css'; // Asegúrate de tener estilos adicionales si es necesario
import Auth from './pages/Auth'; // Componente de autenticación

const Header = ({ user, onLogin, onLogout }) => {
  const [showAuthModal, setShowAuthModal] = useState(false); // Controla la visibilidad del modal
  const [isLoginMode, setIsLoginMode] = useState(true); // Alternar entre login y registro

  const handleLoginClick = () => {
    setIsLoginMode(true);
    setShowAuthModal(true);
  };

  const handleRegisterClick = () => {
    setIsLoginMode(false);
    setShowAuthModal(true);
  };

  const handleLogout = () => {
    onLogout(); // Desloguear al usuario
    alert('Sesión cerrada.');
  };

  return (
    <nav className="header">
      <h1>Todolindo</h1>
      <div className="links">
        <Link to="/">Home</Link>
        <Link to="/offers">Ofertas</Link>
        <Link to="/cart">Carrito</Link>
        <Link to="/help">Ayuda</Link>
        {user && user.role === 'admin' && <Link to="/admin">Admin</Link>}
        {user ? (
          <>
            <span>Bienvenido, {user.name}</span>
            <button className="header-button" onClick={handleLogout}>
              Cerrar Sesión
            </button>
          </>
        ) : (
          <>
            <button className="header-button" onClick={handleLoginClick}>
              Iniciar Sesión
            </button>
            <button className="header-button" onClick={handleRegisterClick}>
              Registrarse
            </button>
          </>
        )}
      </div>

      {/* Modal de autenticación */}
      {showAuthModal && (
        <div className="auth-modal">
          <div className="auth-modal-content">
            <Auth
              onLogin={(userData) => {
                onLogin(userData); // Loguear al usuario
                setShowAuthModal(false); // Cerrar modal
              }}
              isLoginMode={isLoginMode}
            />
            <button
              className="auth-modal-close"
              onClick={() => setShowAuthModal(false)}
            >
              X
            </button>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Header;
