import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const allProducts = [
  { id: 1, name: 'Termo Lumilagro', price: 1000, category: 'Termos' },
  { id: 2, name: 'Botella Metálica', price: 1200, category: 'Botellas' },
  { id: 3, name: 'Mate de Madera', price: 800, category: 'Mates' },
  { id: 4, name: 'Sartén de Teflón', price: 1500, category: 'Cocina' },
];

const Home = () => {
  const [products, setProducts] = useState(allProducts);
  const [searchTerm, setSearchTerm] = useState('');
  const [maxPrice, setMaxPrice] = useState('');

  const filterProducts = () => {
    const filtered = allProducts.filter(
      (p) =>
        p.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
        (!maxPrice || p.price <= maxPrice)
    );
    setProducts(filtered);
  };

  return (
    <div className="container">
      {/* Sección de Bienvenida */}
      <div className="welcome-section">
        <h1>Bienvenido a Todo Lindo</h1>
      </div>

      {/* Buscador de productos */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Buscar productos..."
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <input
          type="number"
          placeholder="Precio máximo"
          onChange={(e) => setMaxPrice(e.target.value)}
        />
        <button onClick={filterProducts}>Buscar</button>
      </div>

      {/* Grilla de productos */}
      <div className="product-grid">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <h3>{product.name}</h3>
            <p>Precio: ${product.price}</p>
            <Link to={`/product/${product.id}`}>
              <button>Ver Detalles</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
