import React, { useState } from "react";
import { Link } from "react-router-dom";

const allProducts = [
  { id: 1, name: "Termo Lumilagro", price: 1000, category: "Termos" },
  { id: 2, name: "Botella Metálica", price: 1200, category: "Botellas" },
  { id: 3, name: "Mate de Madera", price: 800, category: "Mates" },
  { id: 4, name: "Sartén de Teflón", price: 1500, category: "Cocina" },
];

const Home = () => {
  const [products, setProducts] = useState(allProducts);
  const [searchTerm, setSearchTerm] = useState("");
  const [maxPrice, setMaxPrice] = useState("");
  const [cartCount, setCartCount] = useState(0);

  const filterProducts = () => {
    const filtered = allProducts.filter(
      (p) =>
        p.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
        (!maxPrice || p.price <= maxPrice)
    );
    setProducts(filtered);
  };

  const addToCart = () => {
    setCartCount(cartCount + 1);
  };

  return (
    <div className="main-container">
      {/* Header con carrito */}
      <div className="header-bar">
        <div className="welcome-section">
          <h1>Bienvenido a Todo Lindo</h1>
          <h3>Tu lugar ideal para encontrar productos increíbles</h3>
        </div>
        <div className="cart-icon">
          <img src="/catalog_images/cart.png" alt="Carrito" />
          <span className="cart-count">{cartCount}</span>
        </div>
      </div>

      {/* Filtros y Productos */}
      <div className="content-wrapper">
        {/* Sección de filtros */}
        <div className="filter-section">
          <h3>Filtrar productos</h3>
          <input
            type="number"
            placeholder="Precio máximo"
            onChange={(e) => setMaxPrice(e.target.value)}
          />
          <select onChange={(e) => setSearchTerm(e.target.value)}>
            <option value="">Todos</option>
            <option value="Cocina">Cocina</option>
            <option value="Termos">Termos</option>
            <option value="Mates">Mates</option>
            <option value="Botellas">Botellas</option>
          </select>
          <button onClick={filterProducts}>Aplicar</button>
        </div>

        {/* Grilla de productos */}
        <div className="product-grid">
          {products.map((product) => (
            <div key={product.id} className="product-card">
              <h3>{product.name}</h3>
              <p>Precio: ${product.price}</p>
              <div className="buttons">
                <button className="add-to-cart" onClick={addToCart}>
                  Agregar al Carrito
                </button>
                <Link to={`/product/${product.id}`}>
                  <button className="details">Detalles</button>
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
