import { Link } from 'react-router-dom';
import styled from 'styled-components';

const NavBar = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
  background-color: #ff7f50;
  color: white;

  a {
    color: white;
    margin: 0 0.5rem;
    text-decoration: none;
    font-weight: bold;

    &:hover {
      color: #ffc107;
    }
  }

  .dropdown {
    position: relative;

    ul {
      display: none;
      position: absolute;
      background-color: white;
      color: #333;
      list-style: none;
      padding: 0.5rem;
      margin: 0;
      box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
      z-index: 10;
    }

    &:hover ul {
      display: block;
    }

    li {
      padding: 0.5rem;
      cursor: pointer;

      &:hover {
        background-color: #f1f1f1;
      }
    }
  }
`;

const Header = ({ user }) => (
  <NavBar>
    <h1>
      <img src="/logo.png" alt="Logo Todo Lindo" height="40" />
      Todo Lindo
    </h1>
    <div>
      <Link to="/">Home</Link>
      <div className="dropdown">
        <span>Categor√≠as</span>
        <ul>
          <li><Link to="/category/todos">Todos</Link></li>
          <li><Link to="/category/cocina">Cocina</Link></li>
          <li><Link to="/category/termos">Termos</Link></li>
          <li><Link to="/category/mates">Mates</Link></li>
          <li><Link to="/category/botellas">Botellas</Link></li>
        </ul>
      </div>
      <Link to="/offers">Ofertas</Link>
      <Link to="/cart">Carrito</Link>
      <Link to="/help">Ayuda</Link>
      {user && user.role === 'admin' && <Link to="/admin">Admin</Link>}
    </div>
  </NavBar>
);

export default Header;
