import React, { useState } from 'react';
import '../styles.css';

const Checkout = () => {
  const [paymentMethod, setPaymentMethod] = useState('Mercado Pago');
  const [deliveryMethod, setDeliveryMethod] = useState('Andreani');
  const [cardDetails, setCardDetails] = useState({
    cardNumber: '',
    cardName: '',
    expiry: '',
    cvv: '',
  });

  const handleCardChange = (e) => {
    setCardDetails({ ...cardDetails, [e.target.name]: e.target.value });
  };

  const handleCheckout = () => {
    if (paymentMethod === 'Mercado Pago') {
      // Simular integración con Mercado Pago
      window.location.href = 'https://www.mercadopago.com.ar';
    } else {
      console.log('Datos de tarjeta:', cardDetails);
      alert('Procesando pago con tarjeta...');
    }
  };

  return (
    <div className="container">
      <h2>Checkout</h2>

      {/* Método de Pago */}
      <h3>¿Cómo deseas pagar?</h3>
      <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
        <option>Mercado Pago</option>
        <option>Visa</option>
        <option>Mastercard</option>
      </select>

      {/* Formulario de Tarjeta */}
      {paymentMethod !== 'Mercado Pago' && (
        <div className="card-details">
          <h3>Detalles de la Tarjeta</h3>
          <input
            type="text"
            name="cardNumber"
            placeholder="Número de Tarjeta"
            value={cardDetails.cardNumber}
            onChange={handleCardChange}
            maxLength="16"
          />
          <input
            type="text"
            name="cardName"
            placeholder="Nombre en la Tarjeta"
            value={cardDetails.cardName}
            onChange={handleCardChange}
          />
          <div className="card-expiry-cvv">
            <input
              type="text"
              name="expiry"
              placeholder="MM/AA"
              value={cardDetails.expiry}
              onChange={handleCardChange}
              maxLength="5"
            />
            <input
              type="text"
              name="cvv"
              placeholder="CVV"
              value={cardDetails.cvv}
              onChange={handleCardChange}
              maxLength="3"
            />
          </div>
        </div>
      )}

      {/* Método de Envío */}
      <h3>¿Cómo deseas recibir tu pedido?</h3>
      <select value={deliveryMethod} onChange={(e) => setDeliveryMethod(e.target.value)}>
        <option>Andreani</option>
        <option>Correo Argentino</option>
        <option>Retirar en Local (Belgrano 3255)</option>
      </select>

      {/* Botón de Confirmación */}
      <button onClick={handleCheckout}>
        {paymentMethod === 'Mercado Pago' ? 'Pagar con Mercado Pago' : 'Confirmar Pedido'}
      </button>
    </div>
  );
};

export default Checkout;
