import { Link } from 'react-router-dom';
import styled from 'styled-components';

const NavBar = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 2rem;
  background-color: #007bff; /* Nuevo color elegante */
  color: white;

  h1 {
    display: flex;
    align-items: center;
    font-size: 1.5rem;
    color: #ecf0f1; /* Blanco suave */
  }

  h1 img {
    margin-right: 0.5rem;
  }

  .links {
    display: flex;
    align-items: center;
    gap: 1.5rem;

    a {
      color: #ecf0f1; /* Texto blanco suave */
      text-decoration: none;
      font-size: 1rem;
      font-weight: bold;
      transition: color 0.3s ease;

      &:hover {
        color: #f39c12; /* Amarillo suave */
      }
    }
  }

  /* Estilos del dropdown */
  .dropdown {
    position: relative;

    > span {
      cursor: pointer;
      font-weight: bold;
    }

    ul {
      display: none;
      position: absolute;
      top: 100%;
      left: 0;
      background-color: #ffffff; /* Fondo blanco */
      color: #333333; /* Texto oscuro */
      list-style: none;
      margin: 0;
      padding: 0.5rem 0;
      box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
      border-radius: 5px;
      z-index: 1000;
    }

    &:hover ul {
      display: block;
    }

    li {
      padding: 0.5rem 1rem;
      cursor: pointer;
      transition: background-color 0.3s ease;

      &:hover {
        background-color: #f4f4f4;
      }

      a {
        color: #333333;
        text-decoration: none;

        &:hover {
          color: #f39c12;
        }
      }
    }
  }
`;

const Header = ({ user }) => (
  <NavBar>
    <h1>
      <img src="/logo.png" alt="Logo Todo Lindo" height="40" />
      Todo Lindo
    </h1>
    <div className="links">
      <Link to="/">Home</Link>
      <div className="dropdown">
        <span>Categor√≠as</span>
        <ul>
          <li><Link to="/category/todos">Todos</Link></li>
          <li><Link to="/category/cocina">Cocina</Link></li>
          <li><Link to="/category/termos">Termos</Link></li>
          <li><Link to="/category/mates">Mates</Link></li>
          <li><Link to="/category/botellas">Botellas</Link></li>
        </ul>
      </div>
      <Link to="/offers">Ofertas</Link>
      <Link to="/cart">Carrito</Link>
      <Link to="/help">Ayuda</Link>
      {user && user.role === 'admin' && <Link to="/admin">Admin</Link>}
    </div>
  </NavBar>
);

export default Header;
