import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../components/ProductCard';

const products = [
  { id: 1, name: 'Cartera Roja', price: 2000 },
  { id: 2, name: 'Zapatos de Cuero', price: 3500 },
  { id: 3, name: 'Bufanda de Lana', price: 1500 },
  { id: 4, name: 'Camisa Blanca', price: 2500 },
];

const Home = () => (
  <div className="container">
    <h2>Bienvenido a Todo Lindo</h2>
    <div className="flex">
      {products.map((product) => (
        <div key={product.id} className="card">
          <h3>{product.name}</h3>
          <p>Precio: ${product.price}</p>
          <Link to={`/product/${product.id}`}>Ver Detalles</Link>
        </div>
      ))}
    </div>
  </div>
);

export default Home;
