import React from 'react';
import { Link,useNavigate } from 'react-router-dom';
import '../styles.css'; 
import logo from '../catalog_images/logo_todo_lindo.png';
import searchIcon from '../catalog_images/search_icon.png'; 
import cartIcon from '../catalog_images/cart_icon.png'; 
import notificationIcon from '../catalog_images/notification_icon.png'; 
import userIcon from '../catalog_images/user_icon.png'; 

const Header = ({ Link }) => {
  const navigate = useNavigate(); 

  const handleUserClick = () => {
    navigate('/register'); 
  };

  return (
    <nav className="header-container">
      <div className="logo-section">
        <img src={logo} alt="Logo Todo Lindo" className="header-logo" />
        <h1 className="header-title">Todo Lindo</h1>
      </div>
      <div className="nav-links">
        <div className="dropdown">
          <span>Explorar por categoría</span>
          <ul>
            <li><Link to="/category/bazar">Bazar</Link></li>
            <li><Link to="/category/jugueteria">Juguetería</Link></li>
            <li><Link to="/category/libreria">Librería</Link></li>
            <li><Link to="/category/accesorios">Accesorios</Link></li>
          </ul>
        </div>
        <Link to="/offers" className="offers-button">OFERTAS EXCLUSIVAS</Link>
        <div className="search-bar">
          <input type="text" placeholder="Buscar..." />
          <button>
            <img src={searchIcon} alt="Buscar" />
          </button>
        </div>
      </div>
      <div className="icons-section">
        <Link to="/cart">
          <img src={cartIcon} alt="Carrito" className="header-icon" />
        </Link>
        <Link to="/notifications">
          <img src={notificationIcon} alt="Notificaciones" className="header-icon" />
        </Link>
        <button onClick={handleUserClick} className="user-button">
          <img src={userIcon} alt="Usuario" className="header-icon" />
        </button>
      </div>
    </nav>
  );
};

export default Header;
